function navAnimations(){const n=document.querySelector("nav");n.style.opacity=1,n.classList.add("animate__animated","animate__bounceInDown")}function asideAnimations(){const n=document.querySelectorAll(".card-nav");n.forEach(function(n){n.style.opacity=1,n.classList.add("animate__animated","animate__bounceInRight")})}function footerAnimations(){const n=document.querySelector("footer");n.style.opacity=1,n.classList.add("animate__animated","animate__bounceInUp")}function mainAnimations(){const n=document.querySelector("main");n.style.opacity=1,n.classList.add("animate__animated","animate__bounceInUp","animate__delay-1s")}window.onload=function(){navAnimations(),asideAnimations(),footerAnimations(),mainAnimations()};